mapper.select:
SELECT qa.*, u.username, q.questionnaire_name FROM questionnaire_answer qa JOIN user u ON qa.user_id = u.id JOIN questionnaire q ON qa.questionnaire_id = q.id JOIN (SELECT id FROM questionnaire WHERE project_id = #{id}) qn ON qn.id = qa.questionnaire_id WHERE (u.username LIKE CONCAT('%', #{searchKeyWord}, '%') OR q.questionnaire_name LIKE CONCAT('%', #{searchKeyWord}, '%'))
ORDER BY create_date ${sort}
LIMIT #{pageSize} OFFSET #{offset}
