mapper.update:
DELETE q FROM questionnaire_question q LEFT JOIN (
SELECT #{item.id} AS questionnaire_id, #{item.sequence} AS sequence, #{item.star} AS star, #{item.questionContent} AS question_content, #{item.questionId} AS question_id
) i ON q.questionnaire_id = i.questionnaire_id AND q.question_id = i.question_id WHERE q.questionnaire_id = #{questionEntityList[0].id} -- Assuming the first item in the list contains the questionnaire ID AND i.questionnaire_id IS NULL;
INSERT INTO questionnaire_question (questionnaire_id, sequence, star, question_content, question_id) SELECT questionnaire_id, sequence, star, question_content, question_id FROM (
SELECT #{item.id} AS questionnaire_id, #{item.sequence} AS sequence, #{item.star} AS star, #{item.questionContent} AS question_content, #{item.questionId} AS question_id
) i ON DUPLICATE KEY UPDATE sequence = VALUES(sequence), star = VALUES(star), question_content = VALUES(question_content);

