mapper.select:
SELECT *
FROM questionnaire

    WHERE deleted = 0


    WHERE ${type} = 1 AND deleted = 0


    WHERE ${type} = 1

AND questionnaire_name LIKE CONCAT('%', #{searchKeyWord}, '%')
AND project_id = #{id}
ORDER BY ${sortType} ${sort}
LIMIT #{pageSize} OFFSET #{offset}

mapper.update:
update questionnaire

    questionnaire_name = #{questionnaireName},
    questionnaire_description = #{questionnaireDescription}

where id = #{id}

update questionnaire
set star = 1
where id = #{id}

update questionnaire
set star = 0
where id = #{id}

update questionnaire
set deleted = 1
where id = #{id}

update questionnaire
set deleted = 0
where id = #{id}

mapper.delete:
delete
from questionnaire
where deleted = 1 AND project_id = #{id}
